mapper.select:
SELECT DISTINCT topic.topic_id, topic_name, max_person, end_time, topic_type, teacher.id, college, uname FROM topic LEFT JOIN teacher ON topic.id = teacher.id LEFT JOIN user ON teacher.id = user.id WHERE topic.end_time > CURRENT_TIMESTAMP and topic.max_person > ( SELECT COUNT(*) FROM choice as c WHERE c.choice_state = 1 and topic.topic_id = c.topic_id )
and topic.topic_id not in ( SELECT c.topic_id FROM choice as c WHERE c.id = #{student_id} )
limit #{page.startPos}, #{page.pageSize}

SELECT DISTINCT COUNT(*) FROM topic LEFT JOIN teacher ON topic.id = teacher.id WHERE topic.end_time > CURRENT_TIMESTAMP and topic.max_person > ( SELECT COUNT(*) FROM choice as c WHERE c.choice_state = 1 and topic.topic_id = c.topic_id )
and teacher.college = #{search.college}
and teacher.id = #{search.teacher_id}
and topic.topic_name like concat('%', #{search.topic_name}, '%')

SELECT topic.topic_id, topic_name, max_person, teacher.id, uname, college, end_time FROM topic LEFT JOIN teacher ON topic.id = teacher.id LEFT JOIN user ON user.id = teacher.id WHERE topic.end_time > CURRENT_TIMESTAMP and topic.max_person > ( SELECT COUNT(*) FROM choice as c WHERE c.choice_state = 1 and topic.topic_id = c.topic_id )
and teacher.college = #{search.college}
and teacher.id = #{search.teacher_id}
and topic.topic_name like concat('%', #{search.topic_name}, '%')
limit #{page.startPos}, #{page.pageSize}

SELECT topic.topic_id, topic_name, max_person, SUM(IF(choice.choice_state = 0, 1, 0)) as new_person FROM topic LEFT JOIN teacher ON topic.id = teacher.id LEFT JOIN choice ON topic.topic_id = choice.topic_id WHERE topic.id = #{search.teacher_id}
and not (topic.max_person > ( SELECT COUNT(*) FROM choice as c WHERE c.choice_state = 1 and topic.topic_id = c.topic_id ))
and topic.max_person > ( SELECT COUNT(*) FROM choice as c WHERE c.choice_state = 1 and topic.topic_id = c.topic_id )
and topic.topic_name like concat('%', #{search.topic_name}, '%')
GROUP BY topic.topic_id
limit #{page.startPos}, #{page.pageSize}

SELECT DISTINCT COUNT(*) FROM topic LEFT JOIN teacher ON topic.id = teacher.id WHERE topic.end_time > CURRENT_TIMESTAMP and topic.max_person > ( SELECT COUNT(*) FROM choice as c WHERE c.choice_state = 1 and topic.topic_id = c.topic_id )
and teacher.college = #{search.college}
and teacher.id = #{search.teacher_id}
and topic.topic_name like concat('%', #{search.topic_name}, '%')

SELECT topic.topic_id, topic_name, max_person, teacher.id, uname, college, end_time FROM topic LEFT JOIN teacher ON topic.id = teacher.id LEFT JOIN user ON user.id = teacher.id WHERE topic.end_time > CURRENT_TIMESTAMP and topic.max_person > ( SELECT COUNT(*) FROM choice as c WHERE c.choice_state = 1 and topic.topic_id = c.topic_id )
and teacher.college = #{search.college}
and teacher.id = #{search.teacher_id}
and topic.topic_name like concat('%', #{search.topic_name}, '%')
limit #{page.startPos}, #{page.pageSize}

SELECT max_person
FROM topic
WHERE topic_id = #{topic_id}

SELECT COUNT(*)
FROM choice
WHERE choice.id = #{student_id}

mapper.delete:
DELETE FROM choice
WHERE topic_id = #{topic_id}
AND id = #{student_id}

mapper.update:
UPDATE choice
SET choice.choice_state = 1
WHERE topic_id = #{topic_id}
AND id = #{student_id}

mapper.delete:
DELETE FROM choice
WHERE topic_id = #{topic_id}
AND id = #{student_id}
AND choice_state = 0
